(198, 47, 'exp_templates', 'template_data', 1371157654, 1, '<!DOCTYPE html>\n<!--[if IE 8]><html class="no-js lt-ie9" lang="en"><![endif]-->\n<!--[if gt IE 8]><!--><html class="no-js" lang="en"><!--<![endif]-->\n{embed="global_embeds/.header"}\n	<body class="articles">\n		{embed="global_embeds/.header2"}\n                {embed="global_embeds/.menu-top"}\n		<div class="row" id="content" role="main">\n			<div class="large-8 large-centered columns">\n\n				{exp:channel:entries channel="Article" limit="1"}\n				<div class="panel full-article">\n					<a href="{url_title_path=''article/''}">\n						<div class="ap-img-wrap big">\n						<span>\n					{if featured_image !=''''}\n						<img src="{featured_image}" alt="{headline}" />\n					{if:else}\n						<img src="http://ae53674c1338ef56a4c3-1da99be3715259cf218bbe827675f47b.r75.cf2.rackcdn.com/{article_category}-generic-banner.jpg" alt="{article_category}" />\n					{/if}\n						</span>\n						</div>\n					</a>\n					<h1 class="large-9 columns">{headline}</h1>\n					<div class="article-meta large-3 columns">\n						<h3><a href="{author_link}">{article_author}</a></h3>\n						<h4>{author_description}</h4>\n						<p class="date">{article_date format="%F %d, %Y"}</p>\n					</div>\n					{if logged_in}\n						{if webex_media_url !=''''}\n							<div class="panel webex large-3 columns">\n								<a href="{webex_media_url}"><img src="http://ae53674c1338ef56a4c3-1da99be3715259cf218bbe827675f47b.r75.cf2.rackcdn.com/hero_webex.jpg" alt="webex media" /></a>\n								<h2>{webex_title}</h2>\n								<p>{webex_description}</p>\n							</div>\n						{/if}\n						\n						{if ''{exp:detect_mobile:ismobile}''}\n							{if kalturra_url_full !=''''}\n								{embed="global_embeds/.player_mobile" video="{kalturra_url_full}"}\n							{/if}\n						{if:else}   \n							{if kalturra_url_full !=''''}\n								{embed="global_embeds/.player" video="{kalturra_url_full}"}\n							{/if}\n						{/if}						\n\n						<p>{body}</p>\n					{/if}\n					{if logged_out}\n						{if ''{exp:detect_mobile:ismobile}''}\n							{if kalturra_url_full !=''''}\n								{embed="global_embeds/.player_mobile" video="{kalturra_url_teaser}"}\n							{/if}\n						{if:else}   \n							{if kalturra_url_full !=''''}\n								{embed="global_embeds/.player" video="{kalturra_url_teaser}"}\n							{/if}\n						{/if}							\n						\n						{if webex_media_url !=''''}\n							<div class="panel  webex large-3 columns">\n								<img src="http://ae53674c1338ef56a4c3-1da99be3715259cf218bbe827675f47b.r75.cf2.rackcdn.com/hero_anon.jpg" alt="attached media" />\n								<h2>{webex_title}</h2>\n								<p>{webex_description}</p>\n							</div>\n						{/if}\n					<p><?php $x = ''{body}''; \n					        //echo $x;\nfunction truncateHtml($text, $length = 100, $ending = ''...'', $exact = false, $considerHtml = true) {\nif ($considerHtml) {\n// if the plain text is shorter than the maximum length, return the whole text\nif (strlen(preg_replace(''/<.*?>/'', '''', $text)) <= $length) {\nreturn $text;\n}\n// splits all html-tags to scanable lines\npreg_match_all(''/(<.+?>)?([^<>]*)/s'', $text, $lines, PREG_SET_ORDER);\n$total_length = strlen($ending);\n$open_tags = array();\n$truncate = '''';\nforeach ($lines as $line_matchings) {\n// if there is any html-tag in this line, handle it and add it (uncounted) to the output\nif (!empty($line_matchings[1])) {\n// if it''s an "empty element" with or without xhtml-conform closing slash\nif (preg_match(''/^<(\\s*.+?\\/\\s*|\\s*(img|br|input|hr|area|base|basefont|col|frame|isindex|link|meta|param)(\\s.+?)?)>$/is'', $line_matchings[1])) {\n// do nothing\n// if tag is a closing tag\n} else if (preg_match(''/^<\\s*\\/([^\\s]+?)\\s*>$/s'', $line_matchings[1], $tag_matchings)) {\n// delete tag from $open_tags list\n$pos = array_search($tag_matchings[1], $open_tags);\nif ($pos !== false) {\nunset($open_tags[$pos]);\n}\n// if tag is an opening tag\n} else if (preg_match(''/^<\\s*([^\\s>!]+).*?>$/s'', $line_matchings[1], $tag_matchings)) {\n// add tag to the beginning of $open_tags list\narray_unshift($open_tags, strtolower($tag_matchings[1]));\n}\n// add html-tag to $truncate''d text\n$truncate .= $line_matchings[1];\n}\n// calculate the length of the plain text part of the line; handle entities as one character\n$content_length = strlen(preg_replace(''/&[0-9a-z]{2,8};|&#[0-9]{1,7};|[0-9a-f]{1,6};/i'', '' '', $line_matchings[2]));\nif ($total_length+$content_length> $length) {\n// the number of characters which are left\n$left = $length - $total_length;\n$entities_length = 0;\n// search for html entities\nif (preg_match_all(''/&[0-9a-z]{2,8};|&#[0-9]{1,7};|[0-9a-f]{1,6};/i'', $line_matchings[2], $entities, PREG_OFFSET_CAPTURE)) {\n// calculate the real length of all entities in the legal range\nforeach ($entities[0] as $entity) {\nif ($entity[1]+1-$entities_length <= $left) {\n$left--;\n$entities_length += strlen($entity[0]);\n} else {\n// no more characters left\nbreak;\n}\n}\n}\n$truncate .= substr($line_matchings[2], 0, $left+$entities_length);\n// maximum lenght is reached, so get off the loop\nbreak;\n} else {\n$truncate .= $line_matchings[2];\n$total_length += $content_length;\n}\n// if the maximum length is reached, get off the loop\nif($total_length>= $length) {\nbreak;\n}\n}\n} else {\nif (strlen($text) <= $length) {\nreturn $text;\n} else {\n$truncate = substr($text, 0, $length - strlen($ending));\n}\n}\n// if the words shouldn''t be cut in the middle...\nif (!$exact) {\n// ...search the last occurance of a space...\n$spacepos = strrpos($truncate, '' '');\nif (isset($spacepos)) {\n// ...and cut the text in this position\n$truncate = substr($truncate, 0, $spacepos);\n}\n}\n// add the defined ending to the text\n$truncate .= $ending;\nif($considerHtml) {\n// close all unclosed html-tags\nforeach ($open_tags as $tag) {\n$truncate .= ''</'' . $tag . ''>'';\n}\n}\nreturn $truncate;\n}\n\n$x = truncateHtml($x);\nprint_r($y);											\n					?></p>\n					<div class="panel join-cta">\n				