(136, 18, 'exp_templates', 'template_data', 1371065869, 3, '<div class="large-12 columns">\n<div class="row" id="articles-grid">\n	<h1 class="large-12 columns">Articles</h1>\n	{exp:channel:entries channel="Article" limit="3"}\n		<div class="large-4 columns preview-block article {article_category}">\n			<div class="panel">\n				<div class="ap-img-wrap">\n					<span>\n					{if article_thumb_image !=''''}\n						<img src="{article_thumb_image}" alt="{headline}" />\n					{if:elseif featured_image !=''''}\n						<img src="{featured_image}" alt="{headline}" />\n					{if:else}\n						<img src="http://ae53674c1338ef56a4c3-1da99be3715259cf218bbe827675f47b.r75.cf2.rackcdn.com/{article_category}-generic-thumb.jpg" alt="{article_category}" />\n					{/if}					</span>\n				</div>\n				<h4>{headline}</h4>\n				<p>{teaser}</p>\n				<p><a class="button" href="{url_title_path=''article''}">Read More</a>\n			</div>\n		</div>\n	{/exp:channel:entries}\n</div>\n</div>');
