INSERT INTO `exp_snippets` (`snippet_id`, `site_id`, `snippet_name`, `snippet_contents`) VALUES
(1, 1, 'foundation-footer-includes', '<script src="{path=''javascript/zepto''}" type="text/javascript"></script>\n<script src="{path=''javascript/jquery''}" type="text/javascript"></script>\n<script src="{path=''javascript/foundation.min''}" type="text/javascript"></script>\n<script src="{path=''javascript/foundation.forms''}" type="text/javascript"></script>\n<script src="{path=''javascript/foundation.reveal''}" type="text/javascript"></script>\n<script src="{path=''javascript/foundation.section''}" type="text/javascript"></script>\n<script src="{path=''javascript/foundation.topbar''}" type="text/javascript"></script>\n<script src="{path=''javascript/foundation.tooltips''}" type="text/javascript"></script>\n<script type="text/javascript">\n	$(document).foundation();\n	// Filtering of articles and highlight of currently selected filter\n	$(document).ready(function(){\n		var urlhash=location.hash.replace("#","");\n		if(!urlhash){\n			$(''div.article'').show();\n		} else if(urlhash == ''all'') {\n			$(''div.article'').show();\n		} else {\n			$(''div.article'').hide();\n			$(''div.article.''+urlhash).show();\n			$(''dl.sub-nav dd'').removeClass(''active'');\n			$(''dl.sub-nav dd a.''+urlhash).parent(''dd'').addClass(''active'');\n		}\n		$(''.sub-nav a'').click(function(){ \n			var classname=$(this).attr(''class'');\n			$(''div.article'').hide();\n			$(''div.article.''+classname).show();\n			if(!classname){\n				$(''div.article'').show();\n			}\n			$(''dl.sub-nav dd'').removeClass(''active'');\n			$(this).parent(''dd'').addClass(''active'');\n		});\n\n\n\n				var currentTallest = 0, currentRowStart = 0, rowDivs = new Array(), $el, topPosition = 0;\n				$(''#articles-grid .preview-block .panel'').each(function() {\n					$el = $(this);\n					topPostion = $el.position().top;\n					if (currentRowStart != topPostion) {\n						// we just came to a new row.  Set all the heights on the completed row\n						for (currentDiv = 0 ; currentDiv < rowDivs.length ; currentDiv++) {\n							rowDivs[currentDiv].height(currentTallest);\n						}\n					// set the variables for the new row\n					rowDivs.length = 0; // empty the array\n					currentRowStart = topPostion;\n					currentTallest = $el.height();\n					rowDivs.push($el);\n				} else {\n					// another div on the current row.  Add it to the list and check if it''s taller\n					rowDivs.push($el);\n					currentTallest = (currentTallest < $el.height()) ? ($el.height()) : (currentTallest);\n				}\n				// do the last row\n				for (currentDiv = 0 ; currentDiv < rowDivs.length ; currentDiv++) {\n					rowDivs[currentDiv].height(currentTallest);\n				}\n				});\n				\n				\n				\n				\n				\n				var currentTallestEvent = 0, currentRowStartEvent = 0, rowDivsEvent = new Array(), $ell, topPositionEvent = 0;\n				$(''#events-grid .preview-block .panel'').each(function() {\n					$ell = $(this);\n					topPostionEvent = $ell.position().top;\n					if (currentRowStartEvent != topPostionEvent) {\n						// we just came to a new row.  Set all the heights on the completed row\n						for (currentDiv = 0 ; currentDiv < rowDivsEvent.length ; currentDiv++) {\n							rowDivsEvent[currentDiv].height(currentTallestEvent);\n						}\n					// set the variables for the new row\n					rowDivsEvent.length = 0; // empty the array\n					currentRowStartEvent = topPostionEvent;\n					currentTallestEvent = $ell.height();\n					rowDivsEvent.push($ell);\n				} else {\n					// another div on the current row.  Add it to the list and check if it''s taller\n					rowDivsEvent.push($ell);\n					currentTallestEvent = (currentTallestEvent < $ell.height()) ? ($ell.height()) : (currentTallestEvent);\n				}\n				// do the last row\n				for (currentDiv = 0 ; currentDiv < rowDivsEvent.length ; currentDiv++) {\n					rowDivsEvent[currentDiv].height(currentTallestEvent);\n				}\n\n\n\n\n\n	});\n\n\n\n\n\n				var currentTallestVisionary = 0, currentRowStartVisionary = 0, rowDivsVisionary = new Array(), $elll, topPositionVisionary = 0;\n				$(''#experts .preview-block .panel'').each(function() {\n					$elll = $(this);\n					topPostionEvent = $elll.position().top;\n					if (currentRowStartVisionary != topPostionEvent) {\n						// we just came to a new row.  Set all the heights on the completed row\n						for (currentDiv = 0 ; currentDiv < rowDivsVisionary.length ; currentDiv++) {\n							rowDivsVisionary[currentDiv].height(currentTallestVisionary);\n						}\n					// set the variables for the new row\n					rowDivsVisionary.length = 0; // empty the array\n					currentRowStartVisionary = topPostionEvent;\n					currentTallestVisionary = $elll.height();\n					rowDivsVisionary.push($elll);\n				} else {\n					// another div on the current row.  Add it to the list and check if it''s taller\n					rowDivsVisionary.push($elll);\n					currentTallestVisionary = (currentTallestVisionary < $elll.height()) ? ($elll.height()) : (currentTallestVisionary);\n				}\n				// do the last row\n				for (currentDiv = 0 ; currentDiv < rowDivsVisionary.length ; currentDiv++) {\n					rowDivsVisionary[currentDiv].height(currentTallestVisionary);\n				}\n\n\n\n\n\n	});\n\n	});\n</script> \n'),
(2, 1, 'modal-windows', '<div id="enroll-modal" class="reveal-modal small">\n	{exp:freemember:register return="global_embeds/confirmation" rules:member_zipcode="required|numeric|min_length[5]"  rules:firstname="required|min_length[5]" error_delimiters=''<span class="error">|</span>''}\n		<div class="row">\n			<h1 class="large-12 columns left">Membership Registration</h1>			\n		</div>\n		<div class="row">\n			<div class="large-6 columns left">\n				<label for="firstname">First Name</label>\n				{field:firstname}\n				{error:firstname}\n			</div>\n			<div class="large-6 columns left">\n				<label for="lastname">Last Name</label>\n				{field:lastname}\n				{error:lastname}\n			</div>\n		</div>\n		<div class="row">\n			<div class="large-6 columns left">\n				<label for="email">Email Address</label>\n				{field:email}\n				{error:email}\n			</div>\n			<div class="large-6 columns right">\n				<label for="member_zipcode">Zip Code</label>\n				<input type="text" name="member_zipcode" id="member_zipcode" value="" required />\n			</div>\n		</div>\n		<!-- USERNAME INPUT SPACE\n		<div class="row">\n			<div class="large-12 columns left">\n				<label for="TKTKTK">Username</label>\n				{field:TKTKTK}\n				{error:TKTKTK}\n			</div>\n		</div>\n		END USERNAME INPUT SPACE -->\n		<div class="row">\n			<div class="large-6 columns left">\n				<label for="password">Create Password</label>\n				{field:password}\n				{error:password}\n			</div>\n			<div class="large-6 columns right">\n				<label for="password_confirm">Confirm Password</label>\n				{field:password_confirm}\n				{error:password_confirm}\n			</div>\n		</div>\n		<div class="row">\n			<div class="large-12 columns full spaced">\n				<label for="member_stage_of_business">Stage of Business</label>\n				<select name="member_stage_of_business" id="member_stage_of_business" required>\n					<option value="thought">Looking into starting one</option>\n					<option value="launching">Getting ready to launch</option>\n					<option value="newb">Just started out</option>\n					<option value="fewthings">We''ve figured out a few things</option>\n					<option value="seasoned">Seasoned veteran</option>\n					{member_stage_of_business_options}\n				</select>\n			</div>\n		</div>\n		<div class="row">\n			<div class="large-12 columns full spaced">\n				<label>\n					<input type="checkbox" id="option">\n					Sign up for the MyVenture newsletter.\n				</label>\n			</div>\n		</div>\n		<div class="row" id="reg-btn-area">\n			<div class="large-3 columns full spaced">\n				<input type="submit" value="Register" />\n			</div>\n			<div class="large-9 columns full spaced">\n				<p>By registering, I agree to the <a href="{path=''home/terms''}" target="_blank">Terms and Conditions</a> of Business Accelerator.</p>\n			</div>\n		</div>\n	{/exp:freemember:register}\n	<a class="close-reveal-modal">&#215;</a>\n</div>\n<div id="login-modal" class="reveal-modal small">\n	{exp:member:login_form return="{segment_1}/{segment_2}/{segment_3}"}\n		<div class="row">\n			<h1 class="large-12 columns left">Member Login</h1>	\n		</div>\n		<div class="row">		\n			<div class="large-12 columns full spaced">\n				<label for="login-username">Email Address</label>\n				<input type="text" name="username" id="login-username" value="" maxlength="32" size="25">\n			</div>\n		</div>\n		<div class="row">\n			<div class="large-12 columns full spaced">\n				<label for="login-password">Password</label>\n				<input type="password" name="password" id="login-password" value="" maxlength="32" size="25">\n			</div>\n		</div>\n		<div class="row">\n			<div class="large-12 columns full spaced">\n				<input type="submit" name="submit" value="Submit">\n				<p><a href="{path=''member/forgot_password''}">Forgot your password?</a></p>\n			</div>\n		</div>\n	{/exp:member:login_form}	\n	<a class="close-reveal-modal">&#215;</a>\n</div>'),
(3, 1, 'reset', '{exp:freemember:forgot_password return="{reset_sent}" error_handling="inline" error_delimiters=''<span class="error">|</span>''}\n\n    <p>\n        <label for="email">Email</label><br />\n        {field:email}<br />\n        {error:email}\n    <p>\n\n    <p>\n        <input type="submit" value="Submit" />\n    </p>\n\n{/exp:freemember:forgot_password}'),
