(30, 1, 3, 'foundation.forms', 'n', 'js', '/*\nJon Toshmatov 5/30\n*/ \nvar sdomain = document.location.hostname;\n \nvar eventid = 0;\nvar conf = ""; var memberid =""; var memberemail =""\nvar memberemail = ""; var membername = "";\n\n$(''.rsvp_button'').on(''click'', function(e){\n	rid = $(this).attr(''id'');\n	var pos = rid.search(''_'')+1;\n	eventid = rid.substring(pos);\n	confirm_rsvp(eventid);\n});\n\n//confirm the input\nfunction confirm_rsvp(eventid){\n	conf = "rsvpconfirm_"+eventid;\n	memberid = $(".memberid").attr("id");\n	memberemail = $(".memberemail").attr("id");\n	membername = $(".membername").attr("id");\n	//$(".rsvp_confirm_popup").remove();\nif (memberid>0){\ncheck_duplicate(memberid, eventid);\n}else{\nalert("Sorry you must be logged on to RSVP");\n}\n\n}\n\n//confirm popup\n$(".rsvp_confirm_popup").on(''mouseout'', function(e){\n	$(this).fadeOut("slow");\n});\n\n\n//check to see if a member has already registered for the event\nfunction check_duplicate(memberid, eventid){\n	mydata = "memberemail="+memberemail+"&memberid="+memberid+"&eventid="+eventid;	\n	$.ajax({\n	    type : ''POST'',\n	    url :  ''http://''+sdomain+''/EE3/events/checkduplicate'',\n            dataType: ''html'',\n	    data : mydata			    \n	}).done(function(msg){ \n		if (msg==1){\n		alert("You already have registered for this event");\n		return false;\n	}\n	\n	save_member(memberid, eventid)\n	});\n}\n\n//saving the member\nfunction save_member(memberid, eventid){\n	mydata = "memberid="+memberid+"&eventid="+eventid;	\n	$.ajax({\n	    type : ''POST'',\n	    url :  ''http://''+sdomain+''/EE3/events/eventsave'',\n            dataType: ''html'',\n	    data : mydata			    \n	}).done(function(msg){\n	\n	$(".rsvp_confirm_popup").html($(".rsvp_confirm_popup").html()+"<hr/> registering the event"+msg);\n\n	if (msg!=1){\n		alert("Event has not been registered \\n member id or event id is missing");\n		return false;\n	}\n\n	$(''.rsvp_button label'').remove();\n	var loader = "<img class=''e