(87, 1, 13, 'index', 'n', 'webpage', '<!DOCTYPE html>\n<!--[if IE 8]><html class="no-js lt-ie9" lang="en"><![endif]-->\n<!--[if gt IE 8]><!--><html class="no-js" lang="en"><!--<![endif]-->\n<?php $user = ee()->session->userdata; ?>\n{embed="global_embeds/.header"}\n	<body class="events">\n		{embed="global_embeds/.header2"}\n                {embed="global_embeds/.menu-top"}  \n\n\n\n	{exp:channel:entries channel="Event" limit="1"}\n		<div class="large-1 columns event">\n			<div class="ba_events">\n			    <div class="event-date">{date_of_event format="%M"} <span>{date_of_event format="%d"}</span></div>\n				<div class="ap-img-wrap">\n					<span>\n					{if image_for_event !=''''}\n						<img class=''event_image'' src="{image_for_event}" alt="{alt_text_for_image_for_event}" />\n					{if:else}\n						<img class=''event_image'' src="http://ae53674c1338ef56a4c3-1da99be3715259cf218bbe827675f47b.r75.cf2.rackcdn.com/{type_of_event}-generic-thumb.jpg" alt="{type_of_event}" />\n					{/if}\n					</span>\n				</div>\n				<h4>{title}</h4>\n				<p>{description_of_event}</p>\n<p>{type_of_event}</p>\n<p>Scheduled: {date_of_event format="%D, %F %d, %Y"} from {start_time_of_event format="%g:%i %A"} to {end_time_of_event format="%g:%i %A"}</p>\n\n\n \n \n\n\n<p>{webex_title_for_event}</p>\n<p>{description_of_webex_for_event}</p>\n<p>{webex_link_for_event}</p>\n<p>{webex_phone_number_for_event}</p> \n<p>{gplus_hangout_link_for_event}</p>				\n<p>{webcast_url_for_event}</p>\n<p>{location_for_event}</p>\n<p>{notes_for_event_location}</p>\n\n \n\n\n<?php\n$mid = $this->EE->session->userdata(''member_id'');\n$eventid = "{entry_id}";\n$where = "WHERE event_id=$eventid and event_member_id=$mid";\n$results = ee()->db->query("SELECT * FROM exp_events_members_list $where");\nif ($results->num_rows() == 0)\n{\n?>\n<div class="rsvp_confirm_popup" style="display:none" id="rsvpconfirm_{entry_id}"></div> \n						<div id="rsvp_{entry_id}" class="button rsvp_button">\n							<label>RSVP</label>\n							